class EmotionalQuotientInsight extends InsightPluginParent implements InsightPlugin {
    public function generateInsight(Instance $instance, $last_week_of_posts, $number_days) {
        parent::generateInsight($instance, $last_week_of_posts, $number_days);
        $this->logger->logInfo("Begin generating insight", __METHOD__.','.__LINE__);
        $filename = basename(__FILE__, ".php");
        $post_dao = DAOFactory::getDAO('PostDAO');
        
        $lexicon = $this->getLexicon();

        $recent_posts = $post_dao->getPostsByUserInRange($instance->network_user_id,$instance->network,
        date('Y-m-d H:i:s',strtotime('yesterday midnight')),date('Y-m-d H:i:s',strtotime('today midnight')));
        foreach ($recent_posts as $post) {
            if (!$post->in_retweet_of_post_id && !$post->in_reply_to_post_id) {
                $post_text = $post->post_text;
                $words_in_post = explode(' ', $post_text);
                $emwt_post = array();
                $emwt_post['general'] = 0.0; 
                $emwt_post['happiness'] = 0.0; 
                $emwt_post['sadness'] = 0.0; 
                $emwt_post['fear'] = 0.0; 
                $emwt_post['anger'] = 0.0; 
                $emwt_post['disgust'] = 0.0; 
                $emwt_post['surprise'] = 0.0; 
                foreach ($words_in_post as $word) {
                    if (isset($word)) {
                        $word = strtolower(rtrim($word,".,!?"));
                        $i = array_search($word, $lexicon['word']);
                        if (is_int($i)) {
                            $emwt_post['general'] += $lexicon['general'][$i]; 
                            $emwt_post['happiness'] += $lexicon['happiness'][$i]; 
                            $emwt_post['sadness'] += $lexicon['sadness'][$i]; 
                            $emwt_post['fear'] += $lexicon['fear'][$i]; 
                            $emwt_post['anger'] += $lexicon['anger'][$i]; 
                            $emwt_post['disgust'] += $lexicon['disgust'][$i]; 
                            $emwt_post['surprise'] += $lexicon['surprise'][$i];
                        }
                    }
                }
                if ($emwt_post['general']) {
                    $emwt_post['happiness'] /= $emwt_post['general'];
                    $emwt_post['sadness'] /= $emwt_post['general'];
                    $emwt_post['fear'] /= $emwt_post['general'];
                    $emwt_post['anger'] /= $emwt_post['general'];
                    $emwt_post['disgust'] /= $emwt_post['general'];
                    $emwt_post['surprise'] /= $emwt_post['general'];
                }

                $max_emwt_post = max($emwt_post['happiness'],$emwt_post['sadness'], $emwt_post['fear'],
                $emwt_post['anger'],$emwt_post['disgust'],$emwt_post['surprise']);
                if ($emwt_post['general'] >= 0.5 && $max_emwt_post > 0) {
                    $em_replies = array();
                    $replies_to_post = $post_dao->getRepliesToPost($post->post_id,$post->network);
                    if (count($replies_to_post) >= 3) {
                        $em_replies_insight = '';
                        foreach ($replies_to_post as $reply) {
                            $reply_text = $reply->post_text;
                            $words_in_reply = explode(' ', $reply_text);
                            $emwt_reply = array();
                            $emwt_reply['general'] = 0.0; 
                            $emwt_reply['happiness'] = 0.0; 
                            $emwt_reply['sadness'] = 0.0; 
                            $emwt_reply['fear'] = 0.0; 
                            $emwt_reply['anger'] = 0.0; 
                            $emwt_reply['disgust'] = 0.0; 
                            $emwt_reply['surprise'] = 0.0; 
                            foreach ($words_in_reply as $word) {
                                if (isset($word)) {
                                    $word = strtolower(rtrim($word,".,!?"));
                                    $i = array_search($word, $lexicon['word']);
                                    if (is_int($i)) {
                                        $emwt_reply['general'] += $lexicon['general'][$i]; 
                                        $emwt_reply['happiness'] += $lexicon['happiness'][$i]; 
                                        $emwt_reply['sadness'] += $lexicon['sadness'][$i]; 
                                        $emwt_reply['fear'] += $lexicon['fear'][$i]; 
                                        $emwt_reply['anger'] += $lexicon['anger'][$i]; 
                                        $emwt_reply['disgust'] += $lexicon['disgust'][$i]; 
                                        $emwt_reply['surprise'] += $lexicon['surprise'][$i];
                                    }
                                }
                            }
                            if ($emwt_reply['general']) {
                                $emwt_reply['happiness'] /= $emwt_reply['general'];
                                $emwt_reply['sadness'] /= $emwt_reply['general'];
                                $emwt_reply['fear'] /= $emwt_reply['general'];
                                $emwt_reply['anger'] /= $emwt_reply['general'];
                                $emwt_reply['disgust'] /= $emwt_reply['general'];
                                $emwt_reply['surprise'] /= $emwt_reply['general'];
                            }

                            $max_emwt_reply = max($emwt_reply['happiness'],$emwt_reply['sadness'],
                            $emwt_reply['fear'],$emwt_reply['anger'],$emwt_reply['disgust'],
                            $emwt_reply['surprise']);
                            if ($emwt_reply['general'] >= (0.8 * $emwt_post['general']) && $max_emwt_reply > 0) {
                                $strong_em = array_keys($emwt_reply,$max_emwt_reply);
                                if ($strong_em[0] == 'happiness') $em_reply = 'happy';
                                elseif ($strong_em[0] == 'sadness') $em_reply = 'sad';
                                elseif ($strong_em[0] == 'fear') $em_reply = 'afraid';
                                elseif ($strong_em[0] == 'anger') $em_reply = 'angry';
                                elseif ($strong_em[0] == 'disgust') $em_reply = 'disgusted';
                                elseif ($strong_em[0] == 'surprise') $em_reply = 'surpirised';
                                $em_replies_insight .= $reply->author_fullname." was ".$em_reply.", ";
                                
                                $em_replies[] = array('reply' => $reply, 'emotions' => $emwt_reply);
                            }
                        }

                        if (count($em_replies)) {
                            $em_replies_insight = rtrim($em_replies_insight,',');
                            $em_replies_insight .= '.';

                            $this->insight_dao->insertInsight('emotional_replies_'.$post->id, $instance->id, $this->insight_date,
                            "Emotional replies:", $this->username."'s post inspired some emotional replies. ".$em_replies_insight,
                            $filename, Insight::EMPHASIS_LOW, serialize(array($post,$em_replies)));
                        }
                    }

                    $strong_em = array_keys($emwt_post,$max_emwt_post);
                    if ($strong_em[0] == 'happiness') $em_post_insight = 'a lot of <strong>happiness</strong> about';
                    elseif ($strong_em[0] == 'sadness') $em_post_insight = 'too much <strong>sadness</strong> for';
                    elseif ($strong_em[0] == 'fear') $em_post_insight = 'some <strong>fear</strong> about';
                    elseif ($strong_em[0] == 'anger') $em_post_insight = 'quite a bit of <strong>anger</strong> with';
                    elseif ($strong_em[0] == 'disgust') $em_post_insight = 'a little <strong>disgust</strong> about';
                    elseif ($strong_em[0] == 'surprise') $em_post_insight = 'a lot of <strong>surprise</strong> about';
                    $this->insight_dao->insertInsight('emotional_post_'.$post->id, $instance->id,
                    $this->insight_date, "Emotional post:",
                    $this->username."'s post suggests ".$em_post_insight." the thought expressed.",
                    $filename, Insight::EMPHASIS_LOW, serialize(array($post,$emwt_post)));
                }
            }
        }

        $this->logger->logInfo("Done generating insight", __METHOD__.','.__LINE__);
    }

